#nullable enable // autogenerated

using PVZEngine.Entities;
using PVZEngine.Level;

namespace MVZ2.Vanilla.Entities
{
    [EntityBehaviourDefinition(VanillaEntityBehaviourNames.followParent)]
    public class FollowParentBehaviour : EntityBehaviourDefinition
    {
        public FollowParentBehaviour(string nsp, string name) : base(nsp, name)
        {
        }
        public override void Update(Entity entity)
        {
            base.Update(entity);
            if (entity.Parent.ExistsAndAlive())
            {
                entity.Position = entity.Parent.Position;
                entity.Velocity = entity.Parent.Velocity;
            }
        }
    }
}
