#nullable enable // autogenerated

using PVZEngine.Entities;
using PVZEngine.Level;

namespace MVZ2.Vanilla.Entities
{
    [EntityBehaviourDefinition(VanillaEntityBehaviourNames.removeWithoutParent)]
    public class RemoveWithoutParentBehaviour : EntityBehaviourDefinition
    {
        public RemoveWithoutParentBehaviour(string nsp, string name) : base(nsp, name)
        {
        }
        public override void Update(Entity entity)
        {
            base.Update(entity);
            if (!entity.Parent.ExistsAndAlive())
            {
                entity.Remove();
            }
        }
    }
}
