#nullable enable // autogenerated

using PVZEngine.Entities;
using PVZEngine.Level;

namespace MVZ2.Vanilla.Entities
{
    [EntityBehaviourDefinition(VanillaEntityBehaviourNames.timeoutRemoveWithoutParent)]
    public class TimeoutRemoveWithoutParentBehaviour : EntityBehaviourDefinition
    {
        public TimeoutRemoveWithoutParentBehaviour(string nsp, string name) : base(nsp, name)
        {
        }
        public override void Init(Entity entity)
        {
            base.Init(entity);
            entity.Timeout = entity.GetMaxTimeout();
        }
        public override void Update(Entity entity)
        {
            base.Update(entity);
            if (!entity.Parent.ExistsAndAlive())
            {
                if (entity.Timeout >= 0)
                {
                    entity.Timeout--;
                    if (entity.Timeout <= 0)
                    {
                        entity.Remove();
                    }
                }
            }
            else
            {
                entity.Timeout = entity.GetMaxTimeout();
            }
        }
    }
}
