#nullable enable // autogenerated

using MVZ2.Vanilla.Entities;
using MVZ2.Vanilla.Pickups;
using PVZEngine.Entities;

namespace MVZ2.GameContent.Pickups
{
    public abstract class CollectBehaviour : EntityBehaviourDefinition, ICollectBehaviour
    {
        public CollectBehaviour(string nsp, string name) : base(nsp, name)
        {
        }
        public virtual bool CanAutoCollect(Entity pickup)
        {
            return !pickup.NoAutoCollect() && pickup.IsOnGround;
        }
        public virtual bool CanCollect(Entity pickup)
        {
            return true;
        }
        public virtual void PostCollect(Entity pickup)
        {
        }
    }
}