#nullable enable // autogenerated

using MVZ2.Vanilla.Level;
using PVZEngine.Entities;
using PVZEngine.Level;

namespace MVZ2.Vanilla.Entities
{
    [EntityBehaviourDefinition(VanillaEntityBehaviourNames.pickupTiming)]
    public class PickupTimingBehaviour : EntityBehaviourDefinition
    {
        public PickupTimingBehaviour(string nsp, string name) : base(nsp, name)
        {
        }
        public override void Init(Entity entity)
        {
            base.Init(entity);
            entity.Timeout = entity.GetMaxTimeout();
        }
        public override void Update(Entity pickup)
        {
            base.Update(pickup);
            if (!pickup.IsCollected())
            {
                var level = pickup.Level;
                if (!pickup.IsImportantPickup() && pickup.Timeout >= 0)
                {
                    if (!level.IsHoldingEntity(pickup))
                    {
                        pickup.Timeout--;
                        if (pickup.Timeout <= 0)
                        {
                            pickup.Remove();
                        }
                    }
                }
            }
            else
            {
                pickup.AddPickupCollectedTime(1);
            }
        }
    }
}
