#nullable enable // autogenerated

using MVZ2.Vanilla.Entities;
using PVZEngine.Entities;
using PVZEngine.Level;

namespace MVZ2.GameContent.Projectiles
{
    [EntityBehaviourDefinition(VanillaProjectileNames.beaconMeteor)]
    public class BeaconMeteor : EntityBehaviourDefinition
    {
        public BeaconMeteor(string nsp, string name) : base(nsp, name)
        {
        }
        public override void Init(Entity entity)
        {
            base.Init(entity);
            entity.SetModelProperty("Variant", entity.GetVariant());
        }
        public override void Update(Entity entity)
        {
            base.Update(entity);
            entity.SetModelProperty("Variant", entity.GetVariant());
        }
    }
}