#nullable enable // autogenerated

using MVZ2.Vanilla.Entities;
using PVZEngine.Damages;
using PVZEngine.Entities;
using PVZEngine.Level;

namespace MVZ2.GameContent.Projectiles
{
    [EntityBehaviourDefinition(VanillaProjectileNames.NightmareBall)]
    public class NightmareBall : ProjectileBehaviour
    {
        public NightmareBall(string nsp, string name) : base(nsp, name)
        {
        }
        protected override void PostHitEntity(ProjectileHitOutput hitResult, DamageOutput? damage)
        {
            base.PostHitEntity(hitResult, damage);
            var enemy = hitResult.Other;
            if (enemy.Type != EntityTypes.ENEMY)
                return;
            enemy.InflictDeathMarkBuff(5f, 180, new EntitySourceReference(hitResult.Projectile));
        }
    }
}
