#nullable enable // autogenerated

using System.Collections.Generic;
using MukioI18n;
using MVZ2.GameContent.Enemies;
using MVZ2.Vanilla;
using PVZEngine.Entities;
using PVZEngine.Grids;
using Tools;

namespace MVZ2.GameContent.RandomChinaEvents
{
    [RandomChinaEventDefinition(VanillaRandomChinaEventNames.independenceDay)]
    public class IndependenceDayEvent : RandomChinaEventDefinition
    {
        public IndependenceDayEvent(string nsp, string path) : base(nsp, path, NAME)
        {
        }
        public override void Run(Entity contraption, RandomGenerator rng)
        {
            var level = contraption.Level;

            int variant = UndeadFlyingObject.VARIANT_RED;

            HashSet<LawnGrid> possibleGrids = new HashSet<LawnGrid>();
            possibleGrids.Clear();
            UndeadFlyingObject.FillUFOPossibleSpawnGrids(level, variant, level.Option.RightFaction, possibleGrids);

            var filteredGrids = UndeadFlyingObject.FilterConflictSpawnGrids(level, possibleGrids);

            var grids = filteredGrids.RandomTake(SPAWN_COUNT, rng);
            foreach (var grid in grids)
            {
                UndeadFlyingObject.SpawnAtGrid(grid, variant);
            }
        }
        [TranslateMsg("随机瓷器事件名称", VanillaStrings.CONTEXT_RANDOM_CHINA_EVENT_NAME)]
        public const string NAME = "独立日";
        public const int SPAWN_COUNT = 10;
    }
}
