#nullable enable // autogenerated

using MukioI18n;
using MVZ2.GameContent.Buffs.Armors;
using MVZ2.GameContent.Buffs.Enemies;
using MVZ2.Vanilla;
using MVZ2.Vanilla.Audios;
using MVZ2.Vanilla.Entities;
using PVZEngine.Armors;
using PVZEngine.Buffs;
using PVZEngine.Entities;
using Tools;

namespace MVZ2.GameContent.RandomChinaEvents
{
    [RandomChinaEventDefinition(VanillaRandomChinaEventNames.strength)]
    public class StrengthEvent : RandomChinaEventDefinition
    {
        public StrengthEvent(string nsp, string path) : base(nsp, path, NAME)
        {
        }
        public override void Run(Entity contraption, RandomGenerator rng)
        {
            var level = contraption.Level;
            foreach (var enemy in level.FindEntities(e => e.Type == EntityTypes.ENEMY && contraption.IsHostile(e) && e.ExistsAndAlive()))
            {
                if (!enemy.HasBuff<BigTroubleBuff>())
                {
                    enemy.AddBuff<BigTroubleBuff>();
                }
                foreach (var slot in enemy.GetActiveArmorSlots())
                {
                    var armor = enemy.GetArmorAtSlot(slot);
                    if (!Armor.Exists(armor))
                        continue;
                    if (armor.HasBuff<BigTroubleArmorBuff>())
                        continue;
                    armor.AddBuff<BigTroubleArmorBuff>();
                }
            }
            contraption.PlaySound(VanillaSoundID.growBig);
        }
        [TranslateMsg("随机瓷器事件名称", VanillaStrings.CONTEXT_RANDOM_CHINA_EVENT_NAME)]
        public const string NAME = "VIII-力量";
    }
}
