#nullable enable // autogenerated

using MukioI18n;
using MVZ2.GameContent.Buffs.Enemies;
using MVZ2.GameContent.Enemies;
using MVZ2.Vanilla;
using PVZEngine.Buffs;
using PVZEngine.Definitions;
using PVZEngine.Entities;
using Tools;
using UnityEngine;

namespace MVZ2.GameContent.RandomChinaEvents
{
    [RandomChinaEventDefinition(VanillaRandomChinaEventNames.theEmperor)]
    public class TheEmperorEvent : RandomChinaEventDefinition
    {
        public TheEmperorEvent(string nsp, string path) : base(nsp, path, NAME)
        {
        }
        public override void Run(Entity contraption, RandomGenerator rng)
        {
            var level = contraption.Level;
            foreach (var contrap in level.FindEntities(e => e.Type == EntityTypes.PLANT && contraption.IsFriendly(e) && e.ExistsAndAlive()))
            {
                if (!contrap.HasBuff<DivineShieldBuff>())
                {
                    contrap.AddBuff<DivineShieldBuff>();
                }
            }
        }
        [TranslateMsg("随机瓷器事件名称", VanillaStrings.CONTEXT_RANDOM_CHINA_EVENT_NAME)]
        public const string NAME = "IV-皇帝";
    }
}
