#nullable enable // autogenerated

using MukioI18n;
using MVZ2.GameContent.Enemies;
using MVZ2.Vanilla;
using MVZ2.Vanilla.Audios;
using MVZ2.Vanilla.Entities;
using PVZEngine.Definitions;
using PVZEngine.Entities;
using Tools;
using UnityEngine;

namespace MVZ2.GameContent.RandomChinaEvents
{
    [RandomChinaEventDefinition(VanillaRandomChinaEventNames.theFool)]
    public class TheFoolEvent : RandomChinaEventDefinition
    {
        public TheFoolEvent(string nsp, string path) : base(nsp, path, NAME)
        {
        }
        public override void Run(Entity contraption, RandomGenerator rng)
        {
            var level = contraption.Level;
            foreach (var enemy in level.FindEntities(e => e.Type == EntityTypes.ENEMY && contraption.IsHostile(e) && e.ExistsAndAlive()))
            {
                var pos = enemy.Position;
                pos.x = level.GetEnemySpawnX();
                enemy.Position = pos;
            }
            contraption.PlaySound(VanillaSoundID.timeWarp);
        }
        [TranslateMsg("随机瓷器事件名称", VanillaStrings.CONTEXT_RANDOM_CHINA_EVENT_NAME)]
        public const string NAME = "0-愚者";
    }
}
