#nullable enable // autogenerated

using MukioI18n;
using MVZ2.GameContent.Buffs.Level;
using MVZ2.GameContent.Enemies;
using MVZ2.Vanilla;
using MVZ2.Vanilla.Audios;
using MVZ2.Vanilla.Entities;
using PVZEngine.Buffs;
using PVZEngine.Definitions;
using PVZEngine.Entities;
using Tools;
using UnityEngine;

namespace MVZ2.GameContent.RandomChinaEvents
{
    [RandomChinaEventDefinition(VanillaRandomChinaEventNames.theStar)]
    public class TheStarEvent : RandomChinaEventDefinition
    {
        public TheStarEvent(string nsp, string path) : base(nsp, path, NAME)
        {
        }
        public override void Run(Entity contraption, RandomGenerator rng)
        {
            var level = contraption.Level;
            var buff = level.NewBuff<BeaconMeteorBuff>();
            BeaconMeteorBuff.SetFaction(buff, contraption.GetFaction());
            BeaconMeteorBuff.SetDamage(buff, contraption.GetDamage() * 9);
            BeaconMeteorBuff.SetCount(buff, 10);
            BeaconMeteorBuff.SetRNG(buff, new RandomGenerator(rng.Next()));
            BeaconMeteorBuff.GetTimer(buff)?.Let(timer => timer.ResetTime(1));
            level.AddBuff(buff);
        }
        [TranslateMsg("随机瓷器事件名称", VanillaStrings.CONTEXT_RANDOM_CHINA_EVENT_NAME)]
        public const string NAME = "XVII-星星";
    }
}
