#nullable enable // autogenerated

using MVZ2.GameContent.Damages;
using MVZ2.Vanilla.Audios;
using MVZ2.Vanilla.Shells;
using PVZEngine.Damages;
using PVZEngine.Level;

namespace MVZ2.GameContent.Shells
{
    [ShellDefinition(VanillaShellNames.ice)]
    public class IceShell : ShellDefinition
    {
        public IceShell(string nsp, string name) : base(nsp, name)
        {
            SetProperty(VanillaShellProps.BLOCKS_SLICE, true);
        }
        public override void EvaluateDamage(DamageInput damageInfo)
        {
            base.EvaluateDamage(damageInfo);
            if (damageInfo.Effects.HasEffect(VanillaDamageEffects.IMPACT))
            {
                damageInfo.Multiply(2f);
            }
            if (damageInfo.Effects.HasEffect(VanillaDamageEffects.FIRE))
            {
                damageInfo.Multiply(2f);
            }
        }
    }
}
