    [StageDefinition(VanillaStageNames.ArmorParty)]
    public partial class ArmorParty : StageDefinition
    {
        public ArmorParty(string nsp, string name) : base(nsp, name)
        {

            AddBehaviour(new WaveStageBehaviour(this));
            AddBehaviour(new FinalWaveClearBehaviour(this));
            AddBehaviour(new GemStageBehaviour(this));
            AddBehaviour(new StarshardStageBehaviour(this));

            AddTrigger(LevelCallbacks.POST_ENTITY_INIT, PostEnemyInitCallback, filter: EntityTypes.ENEMY);
        }

        public void PostEnemyInitCallback(EntityCallbackParams param, CallbackResult result)
        {
            var entity = param.entity;
            var level = entity.Level;
            if (level.StageDefinition != this)
                return;


            // 定义盔甲列表  
            var helmetOptions = new NamespaceID[]
            {
        VanillaArmorID.leatherCap,
        VanillaArmorID.ironHelmet,
        VanillaArmorID.mesmerizerCrown,
        VanillaArmorID.bersekerHelmet,
        VanillaArmorID.bedserkerHelmet,
        VanillaArmorID.wickedHermitHat,
        VanillaArmorID.skeletonWarriorHelmet,
        VanillaArmorID.emperorCrown,
        VanillaArmorID.SandstoneHelmet,
        VanillaArmorID.bone_helmet,
        VanillaArmorID.necromancer_helmet,
        VanillaArmorID.nether_helmet,
        VanillaArmorID.netherite_ingot_helmet,
        VanillaArmorID.AngryReverserCrown,
        VanillaArmorID.KingofReverserCrown,
        VanillaArmorID.SantaHat,
        VanillaArmorID.GoldenHelmet,
        VanillaArmorID.RustedIronHelmet,
        VanillaArmorID.CopperHelmet,
        VanillaArmorID.ChainmailHelmet,
        VanillaArmorID.DiamondHelmet,
        VanillaArmorID.NetheriteHelmet,
        VanillaArmorID.BedrockHelmet,
            };
            var ShieldOptions = new NamespaceID[]
            {
        VanillaArmorID.umbrellaShield,
        VanillaArmorID.skeletonWarriorShield,
        VanillaArmorID.reflectiveBarrier,
        VanillaArmorID.nether_vanguard_shield,
        //VanillaArmorID.cannon,
            };

            // 使用实体的随机数生成器随机选择一个盔甲  
            var randomHelmet = helmetOptions[entity.RNG.Next(0, helmetOptions.Length)];
            var randomShield = ShieldOptions[entity.RNG.Next(0, ShieldOptions.Length)];

            // 装备选中的盔甲  
            entity.EquipMainArmor(randomHelmet);

            if (entity.IsEntityOf(VanillaEnemyID.cannoneerZombie))
                return;
            entity.EquipArmorTo(VanillaArmorSlots.shield, randomShield);
        }

        public override void OnSetup(LevelEngine level)
        {
            base.OnSetup(level);
            level.SetStarshardCount(2);
        }

        public override void OnStart(LevelEngine level)
        {
            base.OnStart(level);

            //level.SetSeedSlotCount(7);
            //level.FillSeedPacks(new NamespaceID[]
            //{
            //    VanillaBlueprintID.FromEntity(VanillaContraptionID.smallDispenser),
            //    VanillaBlueprintID.FromEntity(VanillaContraptionID.furnace),
